ENT.Type 			= "anim"
ENT.Base 			= "base_anim"
ENT.PrintName 		= "The Official Gunstrukebox"
ENT.RenderGroup 	= RENDERGROUP_TRANSLUCENT

-- Not Toybox
ENT.Spawnable = false
ENT.AdminSpawnable = true

-- Toybox Stuffs
local GUNSTRU_EntityClassname = ""
do GUNSTRU_EntityClassname = ClassName or "sent_gunstrukebox_player" end

if CLIENT then
	usermessage.Hook(GUNSTRU_EntityClassname .. "_MSG", function(um)
		local id = um:ReadShort()
		
		if id == 1 then
			GAMEMODE:AddNotify("Now playing your Gunstrumental data!", NOTIFY_GENERIC, 2)
		
		elseif id == 0 then
			GAMEMODE:AddNotify("You need to spawn the Gunstrumental SWEP at least once to transfer your Gunstrumental data into the Gunstrukebox!", NOTIFY_ERROR, 7)
			
		elseif id == 2 then
			GAMEMODE:AddNotify("Shutting down!", NOTIFY_GENERIC, 2)
		
		elseif id == 3 then
			GAMEMODE:AddNotify("Use your Gunstrumental SWEP, aim at this and press Right Click!", NOTIFY_GENERIC, 10)
			GAMEMODE:AddNotify("When done, (default E) Use the Gunstrukebox to transfer your music! (WARNING: It does not save it)", NOTIFY_GENERIC, 10)
			
		end
		
	end)
	
end

function ENT:Initialize()
	if SERVER then
		self.GSDATA = {}
		self.GSDATA["asound"] 		= ""
		self.GSDATA["bsound"] 		= ""
		self.GSDATA["aoffset"] 		= 0
		self.GSDATA["boffset"] 		= 0
		self.GSDATA["atrans"] 		= 0
		self.GSDATA["btrans"] 		= 0
		self.GSDATA["offsets"] 		= 0
		self.GSDATA["tempo"] 		= 0
		self.GSDATA["volume"] 		= 0
		
		self.GSPATTERNS = {}
		self.GSPATTERNS[1] = ""
		self.GSPATTERNS[2] = ""
		
		self.NextShoot = 0
		self.IsRunning = false
		
		self.Cursor = 1
		
		self:SetModel( "models/props_lab/citizenradio.mdl" )
		self:PhysicsInit( SOLID_VPHYSICS )
		self:SetMoveType( MOVETYPE_VPHYSICS )
		self:SetSolid( SOLID_VPHYSICS)
		self:SetColor( 206, 239, 97, 255 )
		self:SetUseType( SIMPLE_USE )
		
		umsg.Start( GUNSTRU_EntityClassname .. "_MSG", activator )
			umsg.Short( 3 )
		umsg.End()
		
	else //CLIENT
		chat.AddText(Color(255, 255, 0), "The Official Gunstrukebox", Color(255, 255, 255), " currently contains ", Color(0, 255, 0), #self.Gunstru .. " music sheets", Color(255, 255, 255), " that you can load using ", Color(255, 255, 0), "The Gunstrumental SWEP you can also find on ToyBox." )
		chat.AddText(Color(255, 255, 0), "Last Updated", Color(255, 255, 255), " on the ", Color(0, 255, 0), "8th of January 2011", Color(255, 255, 255), "." )
		timer.Simple(10, function() chat.AddText( Color(255, 255, 255), "To use it, aim at this Gunstrukebox while holding Gunstrumental SWEP, and Right click on the Gunstrumental to open the menu: The available music sheets will display on the General tab." )
		
		end)
		
	end
	
end

function ENT:SpawnFunction( ply, tr )
	if not tr.Hit then return end

	local SpawnPos = tr.HitPos + tr.HitNormal * 32

	local ent = ents.Create( ClassName )
	if not ValidEntity( ent ) then return end
	
	ent:SetPos( SpawnPos )
	ent:Spawn()
	ent:Activate()
	
	local physObj = ent:GetPhysicsObject()
	if physObj:IsValid() then
		physObj:Wake()
		
	end

	return ent

end

function ENT:Use( activator, caller )
	if self.IsRunning then
		umsg.Start( GUNSTRU_EntityClassname .. "_MSG", activator )
			umsg.Short( 2 )
		umsg.End()
		
		self.IsRunning = false
		return
	end
	
	if activator:IsPlayer() then
		if GetGunstrumental and GetGunstrumental() then
		
		self.IsRunning = true
		self.Cursor = 1
		self:ApplyCursorPos()
		
		
			local GS_REMOTE = GetGunstrumental()
			
			umsg.Start( GUNSTRU_EntityClassname .. "_MSG", activator )
				umsg.Short( 1 )
			umsg.End()
			
			for key,v in pairs( self.GSDATA ) do
				local isNumber = type( self.GSDATA[ key ] ) == "number"
				
				self.GSDATA[ key ] = activator:GetInfo( "gs_" .. key )
				if isNumber then
					self.GSDATA[ key ] = tonumber( self.GSDATA[ key ] or 0 ) or 0
					
				else
					self.GSDATA[ key ] = self.GSDATA[ key ] or ""
					
				end
				
			end
			
			//self.GSPATTERNS[1] = GS_REMOTE:GetPattern( activator, 1 )
			//self.GSPATTERNS[2] = GS_REMOTE:GetPattern( activator, 2 )
			self.GSPATTERNS[1] = GS_REMOTE:RetreivePattern( activator, 1 )
			self.GSPATTERNS[2] = GS_REMOTE:RetreivePattern( activator, 2 )
			
		else
			umsg.Start( GUNSTRU_EntityClassname .. "_MSG", activator )
				umsg.Short( 0 )
			umsg.End()
			return false
			
		end
		
	end
	
	return true
	
end



function ENT:GetPrimaryDelay()
	local tempo = self.GSDATA["tempo"]
	return (tempo < 6) and 1 or (6 / tempo)
	
end

function ENT:ShootEffects()
end

function ENT:FireA()
end


function ENT:FireB()
end

function ENT:Think()
	if CLIENT then return false end
	
	if self.IsRunning /*and (CurTime() >= self.NextShoot)*/ then
		self.NextShoot = CurTime() + self:GetPrimaryDelay()
		//print( self:GetPrimaryDelay() )
		
		local could = false
		if self:PrimaryPattern( ) 		then self:FireA() could = true end
		if self:HarmonicsPattern( ) 	then self:FireB() could = true end
		if could					 	then self:ShootEffects() end
		
		self:IncrementCursorPos( )
		
	end
	
    self:NextThink( self.IsRunning and self.NextShoot or (CurTime() + 0.5) )
    return true
	
end


function ENT:ApplyCursorPos( )
	self:SetDTInt( 0, self.Cursor - 1 )
	
end

function ENT:RetreiveCursorPos( )
	return self:GetDTInt( 0, self.Cursor ) + 1
	
end

function ENT:IncrementCursorPos( )
	local sPattern = self.GSPATTERNS[1]
	local sPatternLength = string.len( sPattern )
	
	self.Cursor = self.Cursor + 1
	
	if self.Cursor >= (1 + sPatternLength / 2) then
		self.Cursor = 1
		
	end
	self:ApplyCursorPos( )
	
end

function ENT:PrimaryPattern( )
	local sPattern = self.GSPATTERNS[1]
	local iOffs = self.GSDATA["aoffset"] + self.GSDATA["offsets"] + self.GSDATA["atrans"] * 12
	local sPatternLength = string.len( sPattern )
	
	-- Is sPattern valid ?
	if (sPatternLength < 2) or ((sPatternLength % 2) == 1) then return false end
	
	--- Compressed
	local sNote = string.sub( sPattern, self.Cursor * 2 - 1, self.Cursor * 2)
	local pitch = GetGunstrumental():GetTonePitch( sNote, iOffs )
	
	if pitch then
		if pitch <= 255 then
			WorldSound( self.GSDATA["asound"], self:GetPos(), 85, pitch )
			//self:EmitSound( self.GSDATA["asound"] )
			
		//else
		//	print( "Tried to play note ".. sNote .." which is, with offsets ".. iOffs .. " too high to be played (" .. math.floor(pitch) .. ") !")
			
		end
		
	end
	
	return pitch ~= nil
	
end

function ENT:HarmonicsPattern( )
	local sPattern = self.GSPATTERNS[2]
	local iOffs = self.GSDATA["boffset"] + self.GSDATA["offsets"] + self.GSDATA["btrans"] * 12
	local sPatternLength = string.len( sPattern )

	-- Is sPattern valid ?
	if (sPatternLength < 2) or ((sPatternLength % 2) == 1) then return false end
	
	--- Compressed
	local sNote = string.sub( sPattern, self.Cursor * 2 - 1, self.Cursor * 2)
	local pitch = GetGunstrumental():GetTonePitch( sNote, iOffs )
	
	if pitch then
		if pitch <= 255 then
			WorldSound( self.GSDATA["bsound"], self:GetPos(), 85, pitch )
			//self:EmitSound( self.GSDATA["bsound"], 130, pitch )
			
		//else
		//	print( "Tried to play note ".. sNote .." which is, with offsets ".. iOffs .. " too high to be played (" .. math.floor(pitch) .. ") !")
			
		end
	end
	
	return pitch ~= nil
	
end

